    </style>
    <script>
    document.addEventListener('DOMContentLoaded', function() {
      // Remove the "size" attribute from the captcha input, so width CSS wins!
      var cap = document.querySelector('.wpcaptcha_captcha.input-text');
      if (cap) cap.removeAttribute('size');

      function initFormStyles() {
        // Target the EXACT structure from the template
        const emailField = document.querySelector('#nm-register-wrap p.form-row.form-row-wide, #nm-register-wrap p#reg_email_field');
        if (emailField) {
          emailField.style.cssText = 'background: transparent !important; border: none !important; box-shadow: none !important; padding: 0 !important; margin: 0 0 20px !important; text-align: left !important;';
          const label = emailField.querySelector('label');
          if (label) {
            label.style.cssText = 'display: inline-block !important; float: none !important; text-align: left !important; background: #222 !important; color: #aed604 !important; padding: 6px 12px !important; border-radius: 20px !important; font-size: 12px !important; font-weight: 600 !important; margin-bottom: 10px !important;';
          }
          const input = emailField.querySelector('input');
          if (input) {
            input.style.cssText = 'background: #222 !important; color: #fff !important; border: 2px solid #111 !important; border-radius: 10px !important; padding: 14px 16px !important; font-size: 14px !important; width: 100% !important; box-shadow: none !important;';
          }
        }

        // Center ONLY the main form h2 headers
        const loginHeader = document.querySelector('#nm-login-wrap h2');
        const registerHeader = document.querySelector('#nm-register-wrap h2');
        if (loginHeader) {
          loginHeader.style.margin = '0 auto 30px';
          loginHeader.style.display = 'block';
          loginHeader.style.width = 'fit-content';
        }
        if (registerHeader) {
          registerHeader.style.margin = '0 auto 30px';
          registerHeader.style.display = 'block';
          registerHeader.style.width = 'fit-content';
        }

        // ----- PASSWORD REVEAL ON ALL PASSWORD FIELDS -----
        // Find ALL password fields on the page
        var passwordFields = document.querySelectorAll('input[type="password"]');

        passwordFields.forEach(function(field) {
          if (!field.classList.contains('has-password-reveal')) {
            field.classList.add('has-password-reveal');

            // Check if parent is already a password-input wrapper
            if (!field.parentNode.classList.contains('password-input')) {
              // Create wrapper
              var wrapper = document.createElement('div');
              wrapper.className = 'password-input';
              wrapper.style.position = 'relative';
              wrapper.style.display = 'block';
              wrapper.style.width = '100%';

              // Insert wrapper before the field and move field inside wrapper
              field.parentNode.insertBefore(wrapper, field);
              wrapper.appendChild(field);

              // Create eye button with inline styles to ensure it works
              var eye = document.createElement('button');
              eye.type = 'button'; // Prevent form submission
              eye.className = 'show-password-input';
              eye.innerHTML = '👁';  // Direct eye emoji
              eye.style.background = 'transparent';
              eye.style.border = 'none';
              eye.style.position = 'absolute';
              eye.style.right = '15px';
              eye.style.top = '50%';
              eye.style.transform = 'translateY(-50%)';
              eye.style.color = '#aed604';
              eye.style.fontSize = '16px';
              eye.style.cursor = 'pointer';
              eye.style.zIndex = '10';
              eye.title = "Show/Hide Password";

              wrapper.appendChild(eye);

              // Add click event
              eye.addEventListener('click', function(e) {
                e.preventDefault(); // Prevent any form submission
                field.type = field.type === 'password' ? 'text' : 'password';
              });
            }
          }
        });
      }

      // Run multiple times with delays to ensure it catches all forms
      setTimeout(initFormStyles, 500);
      setTimeout(initFormStyles, 1000);
      setTimeout(initFormStyles, 2000);

      // Watch for DOM changes to catch dynamically loaded forms
      var observer = new MutationObserver(function(mutations) {
        mutations.forEach(function(mutation) {
          if (mutation.addedNodes && mutation.addedNodes.length > 0) {
            initFormStyles();
          }
        });
      });

      observer.observe(document.body, { childList: true, subtree: true });
    });
    </script>
